const fs=require("fs"),path=require("path"),{createCanvas}=require("canvas");function generateBadge(){const e=createCanvas(72,72),a=e.getContext("2d");a.fillStyle="#f44336",a.beginPath(),a.arc(36,36,36,0,2*Math.PI),a.fill(),a.fillStyle="#ffffff",a.beginPath(),a.moveTo(36,18),a.lineTo(54,36),a.lineTo(18,36),a.closePath(),a.fill();const n=e.toBuffer("image/png");fs.writeFileSync(path.join(__dirname,"badge-72x72.png"),n),console.log("Generated badge-72x72.png")}generateBadge();