const fs=require("fs"),path=require("path"),{createCanvas}=require("canvas"),sizes=[72,96,128,144,152,192,384,512];function generateIcon(e){const n=createCanvas(e,e),t=n.getContext("2d");t.fillStyle="#2196f3",t.fillRect(0,0,e,e),t.fillStyle="#ffffff";const a=.6*e,f=.8*e,c=(e-a)/2,i=(e-f)/2;t.fillRect(c,i,a,f),t.fillStyle="#1976d2",t.fillRect(c+.1*a,i,.1*a,f);const l=n.toBuffer("image/png");fs.writeFileSync(path.join(__dirname,`icon-${e}x${e}.png`),l)}sizes.forEach((e=>{generateIcon(e),console.log(`Generated icon-${e}x${e}.png`)}));